export var questionObj = {
  "Qtext": "\n \nSuppose that \\(k \\), \\(l \\), \\(p \\), and \\(q \\) are in proportion. Now prove that, \n\\[\t\n\t\\frac{(k - l )^3}{(l - p )^3} = \\frac{k }{q } \\text{ and, }\n\\]\n\\[\n\t\\sqrt{kl } + \\sqrt{lp } - \\sqrt{pq } = \\sqrt{(k + l - p )(l + p - q )}\n\\]\n\\newline\n\n",
  "Steps": [{
    "id": "s1",
    "n_params": 0,
    "step_txt": "$$\\text{\n \nIt is given that $k $, $l $, $p $, and $q $ are in proportion.}$$\n\\[\n\t\\Rightarrow \\frac{k }{l } = \\frac{l }{p } = \\frac{p }{q } = r \\textrm{ (assume)}\n\\]\n\\[\n\t\\Rightarrow p = rq \n\\]"
  }, {
    "id": "s2",
    "n_params": 0,
    "step_txt": "\n\\[\n\t\\Rightarrow l = rp = r ^2 q \n\\]\n\\[\n\t\\Rightarrow k = rl = r ^3 q \n\\]"
  }, {
    "id": "s3",
    "n_params": 0,
    "step_txt": "\nNow, for the first equation, left hand side of the equation can be written as \n\\[\n\t\\textrm{LHS } = \\frac{(k - l )^3}{(l - p )^3}\n\\]\n\\[\n\t= \\frac{(r ^3 q - r ^2 q )^3}{(r ^2 q - rq )^3}\n\\]"
  }, {
    "id": "s4",
    "n_params": 0,
    "step_txt": "\n\\[\n\t= \\frac{(r ^2 q (r - 1))^3}{(rq (r - 1))^3}\n\\]\n\\[\n\t= r ^3\n\\]"
  }, {
    "id": "s5",
    "n_params": 0,
    "step_txt": "\nRight hand side of the equation can be written as\n\\[\n\t\\frac{k }{q } = \\frac{r ^3 q }{q } = r ^3 \n\\]"
  }, {
    "id": "s6",
    "n_params": 0,
    "step_txt": "$$\\text{\nFrom the above two expressions, we can write that LHS $=$ RHS.\n\nNow, for the second equation, on left hand side we have}\n\\[\n\t\\textrm{LHS } = \\sqrt{kl } + \\sqrt{lp } - \\sqrt{pq }\n\\]\n\\[\n\t= \\sqrt{r ^3 q r ^2 q } + \\sqrt{r ^2 q rq } - \\sqrt{rq q }\n\\]"
  }, {
    "id": "s7",
    "n_params": 0,
    "step_txt": "\n\\[\n\t= q \\sqrt{ r } (r ^2 + r - 1)\n\\]"
  }, {
    "id": "s8",
    "n_params": 0,
    "step_txt": "\nNow, right hand side of the equation becomes\n\\[\n\t\\textrm{RHS } = \\sqrt{(k + l - p )(l + p - q )}\n\\]\n\\[\n\t= \\sqrt{(r ^3 q + r ^2 q - rq ) (r ^2 q + rq - q )}\n\\]"
  }, {
    "id": "s9",
    "n_params": 0,
    "step_txt": "\n\\[\n\t= \\sqrt{rq (r ^2 + r - 1) q (r ^2 + r - 1)}\n\\]\n\\[\n\t= q \\sqrt{r } (r ^2 + r - 1)\n\\]"
  }, {
    "id": "s10",
    "n_params": 0,
    "step_txt": "$$\\text{\nFrom the above two expressions, we can observe that LHS $=$ RHS.\n\nHence proved.\n\\newline\n}$$\n"
  }]
};
